  </div>

  <!-- Body -->
  <div style="padding: 20px 22px 22px 22px;">

    <!-- Name + Type -->
    <div style="
      display: flex;
      align-items: center;
      justify-content: space-between;
      gap: 10px;
      margin-bottom: 14px;
