parameters.jsCode:
// === POP7 Parser (compact + robust for n8n Forms output) ===

// In normal runs, $json is an object.
// This fallback also handles cases where someone tests with an array.
const raw = Array.isArray($json) ? $json[0] : $json;

// ---- helpers ----
function flatten(obj, out = {}) {
  if (obj == null) return out;

  if (typeof obj !== "object") {
    return out;
  }

  if (Array.isArray(obj)) {
    obj.forEach(v => flatten(v, out));
    return out;
  }

  for (const [k, v] of Object.entries(obj)) {
    out[k] = v;
    if (v && typeof v === "object") flatten(v, out);
  }
  return out;
}

// Find the first string leaf anywhere inside a value
function deepFirstString(v) {
  if (typeof v === "string") return v;
  if (!v || typeof v !== "object") return null;

  for (const val of Object.values(v)) {
    const s = deepFirstString(val);
    if (s) return s;
  }
  return null;
}

// Normalize keys for fallback matching
function norm(s) {
  return String(s || "")
    .toLowerCase()
    .replace(/[‚Äú‚Äù‚Äû‚Äü]/g, '"')
    .replace(/[‚Äô‚Äò]/g, "'")
    .replace(/[^a-z0-9\s]/g, " ")
    .replace(/\s+/g, " ")
    .trim();
}

// Try to get value by exact label first, then normalized-key match, then loose contains
function getVal(label) {
  if (raw[label] !== undefined) return raw[label];

  const nlabel = norm(label);
  const keys = Object.keys(raw);

  // normalized exact
  for (const k of keys) {
    if (norm(k) === nlabel) return raw[k];
  }

  // loose contains (good for your scenario key splits)
  for (const k of keys) {
    const nk = norm(k);
    if (nk.includes(nlabel.slice(0, Math.min(40, nlabel.length)))) {
      return raw[k];
    }
  }

  return null;
}

// Extract the Likert number from option text like "4 - Agree"
function extractLikert(v) {
  const s = deepFirstString(v) || "";
  const m = s.match(/\b([1-5])\b/);
  return m ? Number(m[1]) : null;
}

// ---- canonical labels (match your form labels) ----
const Q = {
  MOTIVATION: "What motivates you most about exploring a career in insurance sales?",

  PV2: "I‚Äôm motivated by helping families make better financial decisions, not just earning income",
  PV3: "I can stay committed even when results take months",
  PV4: "I‚Äôm comfortable doing work that requires consistency, not just ‚Äúbig events",

  PT1: "I naturally ask questions before offering solutions",
  PT2: "I enjoy listening to people‚Äôs goals and concerns",
  PT3: "I can explain complex ideas in a simple, calm way",

  PC1: "I can handle rejection without taking it personally",
  PC2: "I‚Äôm willing to start conversations with people I don‚Äôt know",
  PC3: "I‚Äôm comfortable asking for introductions/referrals when appropriate",

  LC1: "I actively seek feedback even when it‚Äôs uncomfortable",
  LC2: "If a mentor gives me a method, I will test it consistently for at least 2‚Äì4 weeks",
  LC3: "I like improving through practice, not just theory",

  GR1: "When I fail, I recover quickly and try again",
  GR2: "I don‚Äôt need daily motivation to stay consistent",
  GR3: "I can handle uncertainty about income early on",

  SE1: "I plan my week with clear targets and follow through",
  SE2: "I track my progress instead of guessing",
  SE3: "I‚Äôm comfortable with daily routines and KPI-based goals",

  ET1: "I would rather lose a sale than push a plan I don‚Äôt believe suits the client",
  ET2: "I value reputation and trust more than short-term gains",
  ET3: "I‚Äôm comfortable recommending ‚Äúdo nothing for now‚Äù if it‚Äôs best for the client",

  SCENARIO_REJECTION: "You approach 20 people this week",
  SCENARIO_SUITABILITY: "A client wants a plan you feel is not suitable, but they insist and are ready to buy today",
  COMMITMENT: "If selected, are you willing to complete a 14-day onboarding sprint that includes daily outreach + reflection?"
};

// ---- build parsed payload ----
const parsed = {
  motivation_text: deepFirstString(getVal(Q.MOTIVATION)),

  likert: {
    PV2: extractLikert(getVal(Q.PV2)),
    PV3: extractLikert(getVal(Q.PV3)),
    PV4: extractLikert(getVal(Q.PV4)),

    PT1: extractLikert(getVal(Q.PT1)),
    PT2: extractLikert(getVal(Q.PT2)),
    PT3: extractLikert(getVal(Q.PT3)),

    PC1: extractLikert(getVal(Q.PC1)),
    PC2: extractLikert(getVal(Q.PC2)),
    PC3: extractLikert(getVal(Q.PC3)),

    LC1: extractLikert(getVal(Q.LC1)),
    LC2: extractLikert(getVal(Q.LC2)),
    LC3: extractLikert(getVal(Q.LC3)),

    GR1: extractLikert(getVal(Q.GR1)),
    GR2: extractLikert(getVal(Q.GR2)),
    GR3: extractLikert(getVal(Q.GR3)),

    SE1: extractLikert(getVal(Q.SE1)),
    SE2: extractLikert(getVal(Q.SE2)),
    SE3: extractLikert(getVal(Q.SE3)),

    ET1: extractLikert(getVal(Q.ET1)),
    ET2: extractLikert(getVal(Q.ET2)),
    ET3: extractLikert(getVal(Q.ET3))
  },

  scenarios: {
    rejection_choice: deepFirstString(getVal(Q.SCENARIO_REJECTION)),
    suitability_choice: deepFirstString(getVal(Q.SCENARIO_SUITABILITY))
  },

  commitment_choice: deepFirstString(getVal(Q.COMMITMENT))
};

// ---- compute domain raw sums (3 items each) ----
function sum(ids) {
  return ids.reduce((acc, id) => acc + (parsed.likert[id] || 0), 0);
}

parsed.domain_raw = {
  PV: sum(["PV2", "PV3", "PV4"]),
  PT: sum(["PT1", "PT2", "PT3"]),
  PC: sum(["PC1", "PC2", "PC3"]),
  LC: sum(["LC1", "LC2", "LC3"]),
  GR: sum(["GR1", "GR2", "GR3"]),
  SE: sum(["SE1", "SE2", "SE3"]),
  ET: sum(["ET1", "ET2", "ET3"])
};

// ---- minimal debug for sanity ----
parsed._debug = {
  has_motivation: !!parsed.motivation_text,
  rejection_choice_found: !!parsed.scenarios.rejection_choice,
  suitability_choice_found: !!parsed.scenarios.suitability_choice,
  commitment_found: !!parsed.commitment_choice
};

return [{ json: parsed }];

